 $cpuname = (Get-computerInfo).CsProcessors.Name ; ((Invoke-WebRequest -Method GET -Uri 'https://www.cpubenchmark.net/cpu.php' -Body @{'cpu'="$cpuname"}).ParsedHtml.getElementsByTagName('span') | where-object {$_.outerHTML -like '<SPAN style="FONT-SIZE: 44px; FONT-FAMILY: Arial, Helvetica, sans-serif; FONT-WEIGHT: bold; COLOR: #f48a18">*</SPAN>'}).innerHTML | Set-Content -Path C:\temp\cpuscore.txt
